import React, { useState, useEffect } from "react";
import "./App.css";
import NasaCard from './components/NasaCard';
import axios from 'axios';


function App() {
    const [nasaData, setNasaData] = useEffect([]);
    const moment = require('moment');
    const [date, setDate] = useState(moment().format("YYYY-MM-DD"));

    useEffect(()=>{
        axios.get(`https://api.nasa.gov/planetary/apod?api_key=fa5O3BQljsEyqfNarlgF5bX4t4TbK6vLbySh9i7H&date=${date}`)
        .then(res => {
            // setDate(res.data.date)
            // setExplanation(res.data.explanation)
            // setTitle(res.data.title)
            // setURL(res.data.url)
            // setCopyright(res.data.copyright)
            setNasaData(res.data);
        })
        .catch(err=> console.log(err));
    }, [date]);

    const selectDifferentDate = e => {
        e.preventDefault();
        setDate(e.target.value);
    }

    return (
        <div id="wrapper">
            <div id="container">
                <NasaCard 
                    date={nasaData.date}
                    explanation={nasaData.explanation}
                    title={nasaData.title}
                    url={nasaData.url}
                    copyright={nasaData.copyright}
                    selectDifferentDate={selectDifferentDate}
                    />
            </div>
        </div>
    );
}

export default App;
